(window.webpackJsonp=window.webpackJsonp||[]).push([[75],{323:function(v,_,t){v.exports=t.p+"assets/img/wechat_qrcode.c0c8a471.png"},666:function(v,_,t){"use strict";t.r(_);var a=t(5),i=Object(a.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("这本书我本来以为就是介绍棉花贸易的，但是看到一半就发现这本书的内容远不止贸易这么简单。书的副标题是「一部资本主义全球史」我觉得这个名字还是挺贴切的，整本书的内容基本上就是用棉花贸易引出了整个资本主义的发展历程。")]),v._v(" "),_("p",[v._v("书的内容稍微有一点学术，但基本上也都是点到为止，整本书还是挺好读的，脉络比较清晰，前后逻辑连贯。")]),v._v(" "),_("p",[v._v("如果你上过政治课的话，你可能会对这句话「资本来到世间，从头到脚，每个毛孔都滴着血和肮脏的东西」有印象。但是可能你也会想我一样困惑，为什么现在看起来很多资本主义国家的人民生活都特别好？这句话到底说的有没有问题。看完这本书你可能能解答这个疑惑。")]),v._v(" "),_("p",[v._v("事实上是因为我们以前对资本主义的发展历史并不了解，所以会对很多经典历史的文献内容产生很大的困惑。")]),v._v(" "),_("p",[v._v("这本书从一定程度上能补上你对资本主义发展史的了解，所以还是很推荐对这方面有了解需求的朋友们看下这本书。同时你也会对资本主义国家的一些政策，比如贸易保护等印象深刻。")]),v._v(" "),_("h2",{attrs:{id:"第1章一种全球性商品的兴起"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#第1章一种全球性商品的兴起"}},[v._v("#")]),v._v(" 第1章一种全球性商品的兴起")]),v._v(" "),_("ol",[_("li",[v._v("这章主要是介绍资本主义到来之前，世界各地棉花种植的起源，和各地人们使用棉花的一些基本情况。")]),v._v(" "),_("li",[v._v("让我意外的是印度棉布的当时比中国的棉布更收外国人的青睐。而且因为棉纺织品在那个年代因为部分是劳动密集型的，所以棉花成了重要的保值产品和交换媒介，货币一般的作用。")]),v._v(" "),_("li",[v._v("关于棉花种植，虽然各地有不同的发展历史，但还是有很多的相同点：棉花种植和加工几乎一直保留着小规模的形式，以家庭生产为主。尽管一些种植者把他们的原棉卖到附近或远方的市场去，而且还有许多统治者强迫耕作者将部分作物用于进贡，但是没有任何种植者仅仅依靠种植棉花生活；相反，他们使自己的经济机会多样化，以尽可能地降低风险。随后的资本主义到来将彻底改变这种家庭作坊式生产棉花的模式。")]),v._v(" "),_("li",[v._v("很神奇的是欧洲之前并没有生产棉花的环境，但是随后在资本主义的发展过程里，欧洲却主导了全世界的棉产品贸易。")])]),v._v(" "),_("h2",{attrs:{id:"第2章缔造战争资本主义"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#第2章缔造战争资本主义"}},[v._v("#")]),v._v(" 第2章缔造战争资本主义")]),v._v(" "),_("ol",[_("li",[v._v("这章节看完之后的感觉很复杂，感觉不知道是机缘巧合还是欧洲人的有意设计，也可能真相是在两者之中，欧洲缔造了棉花贸易的世界格局，从美洲掠夺来的金银，去印度交易得到优质的棉布，用交易得来的棉布再去非洲购买奴隶运去美洲进行殖民地的开垦。感觉这套体系真的挺有想象力的，而且气势恢宏。")]),v._v(" "),_("li",[v._v("以前不太理解哥伦布发现美洲以及达伽马发现好望角的意义，但是在棉花贸易当中这两个地理位置的发现，说是改变了棉花世界的贸易格局也不为过。")]),v._v(" "),_("li",[v._v("上初中的时候历史课本介绍过英国的东印度公司，但对英国的东印度公司到底是干什么的还不太明白，这本书有相关的说明，东印度公司其实就是国家支持的私人的商业公司，但是这公司区别于欧洲本地的商业公司，原因是这种商业公司是对殖民地有主权要求的。书中的原话是「国家特许的公司对遥远土地提出的私人政治权力主张，是对经济权力这一概念革命性的重新定义。国家与私人企业主共享对土地和民众的主权」")]),v._v(" "),_("li",[v._v("欧洲这些人的德行就是不择手段的去硬抢，本来大家是普通竞争，但是这些人想要压低价格，就想办法排斥别的竞争者，然后通过武力手段去压迫殖民地人们去生产商品，最终这些人能用极低的成本获取商品，从中攫取巨额利润。")])]),v._v(" "),_("h3",{attrs:{id:"第3章战争资本主义的收益"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#第3章战争资本主义的收益"}},[v._v("#")]),v._v(" 第3章战争资本主义的收益")]),v._v(" "),_("ol",[_("li",[v._v("战争资本主义的最大收益是催生了工业革命，进一步催生出了工业资本主义。")]),v._v(" "),_("li",[v._v("工业革命最重要的成功并不是增加了国家的出口，而是构建了新型的资本主义，工业资本主义。「制造商、贸易商和政治家构建了一种新形式的资本主义——这种资本主义在19世纪末将会主宰世界上绝大部分地区」")]),v._v(" "),_("li",[v._v("工业革命开始带来现代的市场的概念。「现代国家的统治没有专制君主统治那么“明显可见”，而且由于其权力越来越嵌于非个人的条文、法律和官僚机制，所以它看起来也更为“虚弱”。悖谬的是，工业资本主义在扩大国家权力的同时，也使得国家权力更加不可见。而且，规范市场的不再是国王、领主或老爷的个人权威，也不是古老习俗；相反，市场是由合同、法律和法规严格执行的明确规则形成的。」")]),v._v(" "),_("li",[v._v("工业革命给英国棉纺产业带来的后果是，随着生产效率的急剧提升，英国生产的纺织品占据了原本印度纺织品的全球市场份额，而且棉花给国家带来很大的贸易顺差。让国家的实力更加的雄厚。棉花的产业的革命反过来也主推了工业革命。")]),v._v(" "),_("li",[v._v("战争资本主义和工业资本主义的区别是，在工业资本主义下劳动力是自由流动的，土地是稳定的不能被随意攫取，而是受国家保护的。")])]),v._v(" "),_("h3",{attrs:{id:"第4章攫取劳动力和土地"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#第4章攫取劳动力和土地"}},[v._v("#")]),v._v(" 第4章攫取劳动力和土地")]),v._v(" "),_("ol",[_("li",[v._v("上一章介绍了工业资本主义开始进行大规模的纺纱，这导致一个问题是纺织工业对原料的大规模需求，让全世界的原棉市场供不应求，于是英国等国家开始在全世界的范围内找棉花原料，在奥斯曼，非洲，印度找了一圈发现不行，自己种也不行，最后把目光投向了美洲，刚开始还是中美洲，然后就是南美（主要是巴西）。中美洲当时的生产模式还是种植园模式，从非洲进口奴隶，然后放到种植园强迫他们进行种植，感觉跟后来美国南部种植园的模式很相似。")])]),v._v(" "),_("h3",{attrs:{id:"第5章奴隶制盛行"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#第5章奴隶制盛行"}},[v._v("#")]),v._v(" 第5章奴隶制盛行")]),v._v(" "),_("ol",[_("li",[v._v("这一章继续承接上一章的内容，上一章最后其实讲到中美洲有很多不利于继续大规模生产棉花的因素，比如像是海地这种地方发生了奴隶起义，推翻了法国殖民政权，以及中美洲以及巴西还有很多别的产业和棉花产业争夺劳动力，最后就导致中美洲的棉花生产很快就到极限了，于是北美美国开始隆重登场了。")]),v._v(" "),_("li",[v._v("这一章主要内容就是介绍美国南方种植园的发展模式，以及南方大的种植园是干涉美国政府的决策，为之后的南北战争埋下了伏笔，甚至当时美国大规模的开拓土地都是收到南方政客的支持。")]),v._v(" "),_("li",[v._v("美国南部的气候是很适合种植棉花的，这是美国南方种植园能发展的先决条件，同时因为伊莱惠特尼发明了轧花机之后，棉花加工效率快速增长，这个过程加速了美国南方棉花生产的范围和规模... 当然奴隶被更多的引入，美国的棉花进入并重塑全球市场。")]),v._v(" "),_("li",[v._v("所以其实看到这一章的时候能完全感受到为什么当时南方的种植园主为什么这么抵制废奴主义，因为这涉及到了他们的核心利益。要是没有奴隶美国不可能这么快就出口那么多原棉。事实上是美国的奴隶制造成的廉价棉花破坏了世界上多数地区的制造业。")]),v._v(" "),_("li",[v._v("随着美国出口的原棉占到了英国进口原棉的绝大多份额，英国还是觉得美国原棉可能会因为各种原因出现断供或者供应减少的风险， 为了保证棉花加工工业稳定的原材料供应，英国去全世界别的地方尝试找能供应原棉的地方，比如印度、埃及等等，但最终发现还是美国最稳定。")]),v._v(" "),_("li",[v._v("从这一章的叙述里可以看到，资本是自由流动的，向利润更高的地方流动。美国财政部估计，1791年在巴西投资棉花生产的资本仍比美国高10倍以上。到1801年，仅仅10年之后，美国棉花行业的资本投入就比巴西多了60%。")])]),v._v(" "),_("h3",{attrs:{id:"第6章工业资本主义起飞"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#第6章工业资本主义起飞"}},[v._v("#")]),v._v(" 第6章工业资本主义起飞")]),v._v(" "),_("ol",[_("li",[v._v("这部分主要讲的是工业资本主义从英国蔓延到了整个欧洲，以及介绍了为什么有的地方的工业资本主义没有发展起来，比如埃及，印度，巴西等。")]),v._v(" "),_("li",[v._v("这儿其实有个有意思的，并且我之前没有想清楚的点是，既然英国棉花出口这么挣钱，那为什么英国要把技术给别人呢？看完这章发现英国的确没有把技术给被人，而且还对技术实现了禁运（有点类似今天的世界），至于别的国家是怎么获得英国的技术的，靠的是工业间谍活动（颇有点类似之前华裔工程师从国外窃取一些电车造车的技术到国内）。")]),v._v(" "),_("li",[v._v("工业资本主义能够发展起来的一个重要的因素是国家力量，即国家有没有力量保证工业资本主义的劳动力供应，基础设施建设（比如海陆运输体系），以及最重要的、能不能在工业资本发展初期保护国内的市场不被国外的企业给蚕食。其实就是本地市场保护。")]),v._v(" "),_("li",[v._v("我之前也是没有意识到市场是需要保护的，总是认为市场是需要自由竞争的，但是看了这一章我才明白，工业刚开始发展的时候如果不保护国内市场，那国内的工业是发展不起来的，会被国外的大型企业按到地上摩擦... 这也就是为什么我们国家刚开始的时候也制定了很多政策来保护国内市场，不允许一些东西进口的原因。")])]),v._v(" "),_("h3",{attrs:{id:"第7章动员劳动力"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#第7章动员劳动力"}},[v._v("#")]),v._v(" 第7章动员劳动力")]),v._v(" "),_("ol",[_("li",[v._v("这章主要介绍的是工业革命是需要大量的劳动力的，国家如何帮助资本家去协调劳动力资源，以及的劳动力生存状况。")]),v._v(" "),_("li",[v._v("虽然都是工人，但是当时工人的待遇比现在工人待遇要差很多的了。没想到工业革命刚开始的时候工人的境遇那么惨，这就是为什么马克思会说出那句「资本主义的每个毛孔都滴着血和肮脏的东西」，从当时的时代背景下看的话似乎并不过分。站在现代的角度几乎就难以想象当年无产阶级的困境。而且不光是资本家很凶恶，国家为了利益镇压工人也是毫不留情。")])]),v._v(" "),_("h3",{attrs:{id:"第9章一场震惊世界的战争"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#第9章一场震惊世界的战争"}},[v._v("#")]),v._v(" 第9章一场震惊世界的战争")]),v._v(" "),_("ol",[_("li",[v._v("没想到美国内战会促进印度埃及的经济巨大发展，也促进了很多世界其他地区的棉花经济，美国这一打，感觉把整个世界的棉花经济格局都改变了，全世界的市场已经紧密的联系在一起了。「整个晋西北都打成一锅粥啦！」")]),v._v(" "),_("li",[v._v("美国内战中英法等欧洲国家很多人的反应是，为了自己的利益让美国分裂成南北两个国家，南边继续搞奴隶制提供原棉供应。而美国南方的奴隶主也是这么想的。英国也没有停留在口头上，他们也为美国南方的奴隶主提供了切实的帮助，这算粗暴的干涉国家内政了吧...")]),v._v(" "),_("li",[v._v("针对国内外的压力，美国北方联邦政府的措施是鼓励全世界别的地方都种植棉花，他们积极出口棉籽等相关物料，来帮助别的地方种植棉花，来缓解欧洲的棉荒，以及瓦解美国南方奴隶主的经济势力。")])]),v._v(" "),_("h3",{attrs:{id:"第10章全球重建"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#第10章全球重建"}},[v._v("#")]),v._v(" 第10章全球重建")]),v._v(" "),_("ol",[_("li",[_("p",[v._v("没想到美国在内战后为了继续种棉花会出现这么多的问题？首当其冲的就是之前的黑人奴隶变成自由民之后对继续种植棉花这件事儿并不是太看重了。然后就是很多人购买了种植园希望通过发放工资的方式来让自由民工作，但是关于自由劳动力的理解和购买种植园的「地主」的理解并不相同。最终的结果就是「最终人们在种植园、当地法院、州政府以及华盛顿进行了长达数年的斗争，以确定美国棉花种植地区的新劳动体制的轮廓。」")])]),v._v(" "),_("li",[_("p",[v._v("「实际上，一个为国际市场生产商品的世界不再是自由民的首要关切。出于充分理由，自由民相信保有土地才能保证他们获得新的自由，他们认为自己支持联邦的战争努力和自己在奴隶制下的无偿劳动使他们有权获得这些土地。」时代真是变了啊，我们现在的工人阶级哪还想着拥有自己的土地呢？好像很多人似乎也没有想过这样的问题。但是这在以前是非常现实的、关系到人们生计的大问题。")])]),v._v(" "),_("li",[_("p",[v._v("看到这些黑人奴隶在努力为自己的自由斗争，我真的忍不住为他们鼓掌。")])]),v._v(" "),_("li",[_("p",[v._v("感觉欧洲的工业资本主义直接把印度给干成了自己的原材料批发市场，同时也直接抢走了印度的成品棉市场，印度人民竞争不过，又是英国殖民地，印度人民就只能从事更多的农业劳动来为工业资本主义提供原材料。感觉好惨....")]),v._v(" "),_("p",[v._v("「最晚在1853年，贝拉尔基本上还脱离世界市场，有着以农村为导向的经济，拥有大量的家庭制造业。然而，到了19世纪70年代，贝拉尔的大部分经济活动都围绕在为全球市场生产原棉。一位英国殖民官员在70年代中期观察到，在贝拉尔，“棉花的种植几乎完全是为了出口。国内的家用布的制造受到英国商品进口的冲击，许多织工阶层的人已成为普通劳动者”。当地经济的这种重新定位也迫使人们从事农业劳动，例如班贾拉人（传统上运送棉花的牛车主人）以及纺纱工和织工都发现自己失业了，并越来越依赖从事农业以维持生计。事实上，40年后，一位记者报道，“自从铁路通车以来”，贝拉尔曾经欣欣向荣的棉花制造业几乎全部消失了。」")])]),v._v(" "),_("li",[_("p",[v._v("这个过程真的发生的很神奇，感觉资本有一百种方式让你为他们服务，当然这个过程中，国家也起到非常的大作用")]),v._v(" "),_("p",[v._v("「所有这些重塑全球农村的斗争都有共同之处，那就是国家现在发挥了重要作用。国家制定和实施了新形式的强制手段，取代了奴隶主的直接肉体暴力。这并不意味着人身暴力不存在，但与合同、法律和税收带来的压力相比，这是次要的。随着各国对领土建立新的主权，他们还把主权扩展到了劳工身上，这见证了工业资本主义的新的制度力量。」")])]),v._v(" "),_("li",[_("p",[v._v("同时信贷对催化农民从自给自足的工业转化为为工业资本主义生产也起到很重要的作用。")]),v._v(" "),_("p",[v._v("「受到战争摧残的自耕农现在种植棉花以赚取现金。随着最小的内地市镇都有商人进入，自耕农能轻易地售卖棉花，同时享受更多的制成品、肥料以及重要的信贷渠道。1906年一位德国社会科学家观察到：“这些信贷对于从战争的影响中恢复至关重要，但是一旦陷入信贷体系，农民就被迫种植更多的棉花，这是因为商人只肯接受容易卖掉的作物为抵押。”」")])]),v._v(" "),_("li",[_("p",[v._v("我感觉这章节的内容还介绍了，农业社会向工业社会转型的过程，以及农民阶级向无产阶级转变的过程。")])]),v._v(" "),_("li",[_("p",[v._v("棉花工业的发展最迫切的需要就是劳动力资源，所以国家和资本家制造商通过各种手段，强迫或者是诱导等方式，让农民成为工业社会的劳动力，让工人充分发挥自己的劳动力，甚至从国家层面颁布法律不允许他们流浪，就老老实实干活。还是挺残酷的。资本家和这些工业社会国家的利益是高度一致的。")]),v._v(" "),_("p",[v._v("「在重建后的棉花帝国中，欧洲和北美新获得权力的国家无处不在。毕竟，资本家通过获取劳动力来积累资本的计划和官僚通过控制人口来建构国家的计划是相辅相成的。71在国内和工业资本主义中心地带的斗争中，棉花资本家明白，要改造农村，改造社会，就必须使用国家的力量来巩固财富。各国由于从事战争资本主义获得了新的官僚、法律、军事和基础设施建设能力，在这种能力的支持下，制造商和商人将世界上越来越多的人和地区纳入全球经济之中，特别是纳入为世界市场而从事棉花生产的体系之中。」")])])]),v._v(" "),_("h3",{attrs:{id:"第13章重回全球南方"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#第13章重回全球南方"}},[v._v("#")]),v._v(" 第13章重回全球南方")]),v._v(" "),_("ol",[_("li",[v._v("19 世纪末20 世纪初，工人还是通过工会组织的罢工运动逐渐得到了更好的待遇。正好也是五四运动前后发生的背景。和前面 19 世纪初工人被压迫形成了呼应。")]),v._v(" "),_("li",[v._v("工人组织罢工运动能够成功的原因在一定程度是因为战争，国家需要动员广大人民参军，以及生产军队所需要的各种物资，即国家面临着金钱和人民的压力。从而间接赋予了工人阶级在工厂车间的政治权利。")]),v._v(" "),_("li",[v._v("相对发达的国家因为劳动力的成本越来越高，制造业开始逐渐转移向劳动力成本没那么高的国家，这部分和现在全球供应产业链的转移几乎是一模一样的。区别在于现在制造业供应链条很长，转移的成本可能会更加的高。")]),v._v(" "),_("li",[v._v("发达国家的资本家并没有坐以待毙，他们应对工人劳动成本上升的办法是，想尽办法投资改善生产技术，来增加每个工人的生产效率，这个趋势也延续到了现在，当然尽管如此，劳动力成本还是非常重要。资本家同时也想办法保护国内市场不被别的国家的低价棉布商品侵蚀。")]),v._v(" "),_("li",[v._v("「埃及的故事向全球南方的资本所有者表明，他们需要建立一个支持其国内工业化项目的国家，在殖民主义条件下，这种国家是无法建立的。」民族工业资本主义的发展离不开一个支持它的国家，不仅仅是在关税保护方面，还包括市场保护，基础设施建设，劳动力制度等等。印度实业加的工业发展梦想面对殖民的障碍使得他们必须先要反对殖民主义，建立起自己的国家，否则发展本国工业就是痴人说梦。")])]),v._v(" "),_("h3",{attrs:{id:"最后"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#最后"}},[v._v("#")]),v._v(" 最后")]),v._v(" "),_("p",[v._v("感觉阅读棉花全球贸易的历史，能看清楚很多现在的事情。")]),v._v(" "),_("p",[v._v("比如发达国家现在要求发展中国家执行严格的碳排放其实就是一种不公平对待，他们自己污染了环境那么多年，现在把一些不那么先进的技术和设备留给发展中国家，却要求发展中国家承担超过他们本身承受能力的负担。这种做法本质上就是想要遏制发展中国家的发展速度。")]),v._v(" "),_("p",[v._v("说起贸易保护，没有人比这些传统的发达国家玩的更溜了，虽然现在全球化一体，但是发达国家在贸易吃亏的时候也还是提高关税等措施，他们这么做也就别指责别人进行贸易保护，否则不就是双标吗。")]),v._v(" "),_("hr"),v._v(" "),_("p",[v._v("关注我的微信公众号，我在上面会分享我的日常所思所想。")]),v._v(" "),_("div",{attrs:{align:"center"}},[_("img",{attrs:{src:t(323),width:"40%/"}})])])}),[],!1,null,null,null);_.default=i.exports}}]);